mapper.select:
select * from ORDER_ITEM where man_id=#{man_id}
 

 and  a.order_id=#{order_id}
	
 
 and a.state= #{state}
 
 
 and a.order_date >= #{startDate}


 and a.order_date <= #{endDate}

select * from (select x.*,ROWNUM RN
from(select * from ORDER_ITEM where man_id=#{man_id}
 

 and  a.order_id=#{order_id}
	
 
 and a.state= #{state}
 
 
 and a.order_date >= #{startDate}


 and a.order_date <= #{endDate}
 
 
)X where ROWNUM < =#{end}) where RN >=#{start}

select * from ORDER_ITEM a inner join  MANUFACTURER b
  on a.man_id=b.man_id
 


 and a.man_id=#{man_id}


 and  a.order_id=#{order_id}



 and a.order_date >= #{startDate}


 and a.order_date <= #{endDate}
 
 
 and b.man_name like #{man_name}
 
 
 and a.state= #{state}

select * from (select x.*,ROWNUM RN
from(
select * from ORDER_ITEM a inner join  MANUFACTURER b  on a.man_id=b.man_id
 

 and  a.order_id=#{order_id}


 and a.man_id=#{man_id}


 and a.order_date >= #{startDate}


 and a.order_date <= #{endDate}
 
 
 and b.man_name like #{man_name}
 
 
 and a.state= #{state}


)X where ROWNUM < =#{end}) where RN >=#{start}

select count(a.order_id) from ORDER_ITEM a inner join  MANUFACTURER b  on a.man_id=b.man_id
 

 and  a.order_id=#{order_id}


 and a.man_id=#{man_id}


 and a.order_date >= #{startDate}


 and a.order_date <= #{endDate}
 
 
 and b.man_name like #{man_name}
 

 and a.state= #{state}

select count(order_id) from ORDER_ITEM  where man_id=#{man_id}
 

 and  a.order_id=#{order_id}
	
 
 and a.state= #{state}
 
 
 and a.order_date >= #{startDate}


 and a.order_date <= #{endDate}
