mapper.select:
    select * from UUSER u
    
		
			and u.user_name like #{userName}
		
		
			and u.user_sex = #{userSex}
		
		
			and u.user_birthday >= #{startDate}
		
		
			and u.user_birthday <= #{endDate}
		
		
			and u.user_age >= #{lowerAge}
		
		
			and u.user_age <= #{upperAge}
		
		
			and u.user_Phone like #{userPhone}
		
		
			and u.user_id in (select user_id from UUSER_ROLE_RELATE where role_id in
			
	         	#{roleId}
	        
	        )

    select * from
	(select ROWNUM rn,a.*
	from (select * from UUSER u
		   
	    	
				u.user_name like #{userName}
			
			
				and u.user_sex = #{userSex}
			
			
				and u.user_birthday >= #{startDate}
			
			
				and u.user_birthday <= #{endDate}
			
			
				and u.user_age >= #{lowerAge}
			
			
				and u.user_age <= #{upperAge}
			
			
				and u.user_Phone like #{userPhone}
			
			
				and u.user_id in (select user_id from UUSER_ROLE_RELATE where role_id in
				
		         	#{roleId}
		        
		        )
			
  		   
		  ) a)
		  where rn<=#{rows}*#{page} and rn>=#{rows}*(#{page}-1)

	select count(user_id)
 	from UUSER
 	 
  		
			user_name like #{userName}
		
		
			and user_sex = #{userSex}
		
		
			and user_birthday >= #{startDate}
		
		
			and user_birthday <= #{endDate}
		
		
			and user_age >= #{lowerAge}
		
		
			and user_age <= #{upperAge}
		
		
			and user_Phone like #{userPhone}
		
		
			and user_id in (select user_id from UUSER_ROLE_RELATE where role_id in
			
	         	#{roleId}
	        
	        )
